	(	select rownum rnum, b.*
		from(
				select item.id, name, price, image, location
				from item
				inner join wish
				on item.id = wish.item_id
				where member_id = #{id}
				order by wish.id desc
		)b
		where rownum < = #{end}
	)
	where rnum between #{start} and #{end}
