mapper.select:
select * from
	(	select rownum rnum, b.*
		from(
				select item.id, name, price, image, location, status
				from item
				inner join wish
				on item.id = wish.item_id
				where member_id = #{id}
				order by wish.id desc
		)b
		where rownum < = #{end}
	)
	where rnum between #{start} and #{end}

mapper.delete:
	delete 	from wish
	where 	member_id = #{member_id}
	and					

        	item_id = #{item}

