mapper.update:
update 	qna
set 	BOARD_READCOUNT = BOARD_READCOUNT + 1
where 	BOARD_NUM = #{num}

mapper.select:
select 	* 
from 	qna
where 	BOARD_NUM = #{num}
and		BOARD_PASS = #{pass}

mapper.update:
update 	qna
set 	BOARD_SUBJECT = #{BOARD_SUBJECT}, 
		BOARD_CONTENT = #{BOARD_CONTENT}
where 	BOARD_NUM = #{BOARD_NUM}
